    private void maxbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_maxbtnActionPerformed
        // declare variables
        double num1;
        double num2;
        double max;
        
        num1 = Integer.parseInt(maxtxt.getText());
        num2 = Integer.parseInt(maxtxt2.getText());
        
        max = Math.max (num1, num2);
        maxanslbl.setText (String.valueOf(max));
    }//GEN-LAST:event_maxbtnActionPerformed

    private void minbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_minbtnActionPerformed
       // declare variables
        double num1;
        double num2; 
        double min;
        
        num1 = Integer.parseInt(mintxt.getText());
        num2 = Integer.parseInt(mintxt2.getText());
        
        min = Math.min (num1, num2);
        minanslbl.setText(String.valueOf(min));
        
        
    }//GEN-LAST:event_minbtnActionPerformed

