    private void radianbtnActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_radianbtnActionPerformed
      // declare variables
        double num1; 
        double radian; 
        
        num1 = Integer.parseInt(radiantxt.getText());
        radian = Math.toRadians(num1);
        
        radiananslbl.setText(String.valueOf(radian));
    }//GEN-LAST:event_radianbtnActionPerformed

